import { useState } from "react";
import Axios from "axios";

const useFlip = (initialState) => {
  const [state, setState] = useState(initialState);
  const toggleFlip = () => {
    setState((state) => !state);
  };
  return [state, toggleFlip];
};

const useAxios = () => {};

export { useFlip };
