import { useState } from "react";
import axios from "axios";

const useFlip = (initialState) => {
  const [state, setState] = useState(initialState);
  const toggleFlip = () => {
    setState((state) => !state);
  };
  return [state, toggleFlip];
};

const useAxios = (key, baseUrl) => {
  const [res, setRes] = useState(key);
  const addResData = async (formatter = (data) => data) => {
    const response = await axios.get(`${baseUrl}`);
    setRes((data) => [...data, formatter(response.data)]);
  };
  const clearRes = () => setRes([]);

  return [res, addResData, clearRes];
};

export { useFlip, useAxios };

const [cards, setCards] = useState([]);
const addCard = async () => {
  const response = await axios.get(
    "https://deckofcardsapi.com/api/deck/new/draw/"
  );
  setCards((cards) => [...cards, { ...response.data, id: uuid() }]);
};
