import { useState } from "react";
import axios from "axios";

const useFlip = (initialState) => {
  const [state, setState] = useState(initialState);
  const toggleFlip = () => {
    setState((state) => !state);
  };
  return [state, toggleFlip];
};

const useAxios = (key, baseUrl) => {
  const [res, setRes] = useState(key);
  const addResData = async (formatter = (data) => data, urlQuerys = "") => {
    const response = await axios.get(`${baseUrl}${urlQuerys}`);
    setRes((data) => [...data, formatter(response.data)]);
  };
  const clearRes = () => setRes([]);

  return [res, addResData, clearRes];
};

export { useFlip };
